        const { scale: s, translate: t } = topo.transform;
        const decoded = topo.arcs.map(arc => {
          let x = 0, y = 0;
          return arc.map(([dx, dy]) => {
            x += dx; y += dy;
            return [x * s[0] + t[0], y * s[1] + t[1]];
          });
        });
